import React from "react";
import { connect } from "react-redux";
import Users from "./Users";
import {
  follow,
  unfollow,
  setUsers,
  setTotalUsersCount,
  setCurrentPage,
  isFatchingFunc,
  toggleFollowingProgress
} from "./../../redux/users-reducer";
import {usersAPI} from "./../../api/api"

class UsersContainer extends React.Component {
  componentDidMount() {
    this.props.isFatchingFunc(true);
      usersAPI.getUsers(this.props.currentPage, this.props.pageSize).then(data => {
        this.props.setUsers(data.items);
        this.props.setTotalUsersCount(data.totalCount);
        this.props.isFatchingFunc(false);
      });
  }

  changePage = (page) => {
    this.props.setCurrentPage(page);
    this.props.isFatchingFunc(true);
      usersAPI.getUsers(page, this.props.pageSize)
        .then(data => {
          this.props.isFatchingFunc(false);
          this.props.setUsers(data.items);
      });
  };
  render() {
    return (
      <Users {...this.props} changePage = {this.changePage}/>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    users: state.usersPage.users,
    pageSize: state.usersPage.pageSize,
    totalUsersCount: state.usersPage.totalUsersCount,
    currentPage: state.usersPage.currentPage,
    isFetchingValue: state.usersPage.isFetchingValue,
    flollowingInProgress: state.usersPage.flollowingInProgress,
  };
};


export default connect(
  mapStateToProps, {follow, unfollow, setUsers, setTotalUsersCount, setCurrentPage, isFatchingFunc, toggleFollowingProgress
})(UsersContainer);
